  def valid_attributes
    return {:thought => "This is a thought."}
  end
  
  class ThoughtsController
    def authenticate_user!

    end
  end

  describe "GET index" do
    it "assigns all thoughts as @thoughts" do
      thought = Thought.create! valid_attributes
      get :index
      assigns(:thoughts).should eq([thought])
    end
  end

  describe "GET show" do
    it "assigns the requested thought as @thought" do
      thought = Thought.create! valid_attributes
      get :show, :id => thought.id.to_s
      assigns(:thought).should eq(thought)
    end
  end

  describe "GET new" do
    it "assigns a new thought as @thought" do
      get :new
      assigns(:thought).should be_a_new(Thought)
    end
  end

  describe "GET edit" do
    it "assigns the requested thought as @thought" do
      thought = Thought.create! valid_attributes
      get :edit, :id => thought.id.to_s
      assigns(:thought).should eq(thought)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Thought" do
        expect {
          post :create, :thought => valid_attributes
        }.to change(Thought, :count).by(1)
      end

      it "assigns a newly created thought as @thought" do
        post :create, :thought => valid_attributes
        assigns(:thought).should be_a(Thought)
