        post :create, :thought => {}
        assigns(:thought).should be_a_new(Thought)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Thought.any_instance.stub(:save).and_return(false)
        post :create, :thought => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested thought" do
        thought = Thought.create! valid_attributes
        # Assuming there are no other thoughts in the database, this
